
import { DatabaseLib } from "./database";
import * as $ from "../src/jquery.js";
import { Models } from "./Models.js";
import jsPDF from "jspdf";
import { error } from "jquery";
const selected = localStorage.getItem('selectedNcrId');

document.addEventListener('DOMContentLoaded', function() {
    const btnDownload = document.querySelector('.btn-download');
    if (btnDownload) {
        //Download the pdf
        btnDownload.addEventListener('click', () => {
            console.log('Button encontrado');
            createPDF();      
        });
    } else {
        console.error('El bot√≥n de descarga no se encuentra');
    };
   const usedNumbers = new Set<number>();
   function randomNumber(min: number, max: number): number {
    
    if(usedNumbers.size >= max - min + 1){
        throw new Error('No more unique numbers avaliable');
    }
    let num;
    do{
        num = Math.floor(Math.random() * (max - min + 1)) + min;
    }while(usedNumbers.has(num));

    usedNumbers.add(num);
    return num;
    
   }
   
     //create the pdf
    function createPDF() {
        if (selected) {
            var db = DatabaseLib.Database.get();
            const selectedID = Number(selected);
            const selectedNCR = db.GetNCRByNumber(selectedID);
            const selectedQA = db.GetQAByID(Number(selectedNCR.QualityAssuranceID));
            const selectedEng = db.GetENGByID(Number(selectedNCR.EngineeringID));
            const selectedPur = db.GetPURByID(Number(selectedNCR.PurchasingID));
            const modelStatus = Models.Status[selectedNCR.Status];
            const Status = db.GetSupplierByID(Number(selectedNCR.Status));
    
            if (!selectedNCR || !selectedQA) {
                console.error('No se pudo obtener datos para el NCR o QA');
                return;
            }
            const pdf = new jsPDF('p', 'mm', 'a4');

            const left = 10;
            const top = 10;
            const width = 210;
            const height = 297;
            const contentWidth = width - 2 * left;
            const contentHeight = height - 2 * top;


            const marginLeft = 12; 
            const marginTop = 10; 
            const sectionSpacing = 6; 
            let Y = marginTop + 10;
            let pages = pdf.getNumberOfPages();
            
            pdf.setDrawColor(170,170, 170);
            pdf.setLineWidth(1);
            pdf.rect(left, top, contentWidth, contentHeight); 

            pdf.setFontSize(14);
            pdf.text('CrossFire Canada', marginLeft + 2, Y + 6);
            pdf.text('Internal process document', marginLeft + 120, Y + 6);
            Y += 20;

            pdf.setFontSize(12);
            pdf.text(`Document No.: 0000${selectedNCR.ID}`, marginLeft, Y);
            pdf.text(`Document Title: Non-Conformance Report`, marginLeft + 50, Y);
            pdf.text(`Pages: ${pages}`, marginLeft + 150, Y);
            Y += sectionSpacing;

            // Quality
            pdf.setFontSize(12);
            pdf.text('Quality Portion:', marginLeft, Y);
            Y += sectionSpacing;

            pdf.setFontSize(10);
            pdf.text(`Supplier Name: ${Status.Name}`, marginLeft, Y);
            pdf.text(`NCR Number: ${selectedNCR.NCRNumber}`, marginLeft + 120, Y);
            Y += sectionSpacing;

            pdf.text(`Description of Item: ${selectedQA.ItemDescription}`, marginLeft, Y);
            pdf.text(`PO or Prod No.: ${selectedQA.ProductNo}`, marginLeft + 120, Y);
            Y += sectionSpacing;

            pdf.text(`Description of Defect: ${selectedQA.DefectDescription}`, marginLeft, Y);
            pdf.text(`Sales Order No.: ${selectedQA.OrderNo}`, marginLeft + 120, Y);
            Y += sectionSpacing;

            pdf.text(`Quantity Received: ${selectedQA.QuantityReceived}`, marginLeft, Y);
            pdf.text(`Quantity Defective: ${selectedQA.QuantityDefective}`, marginLeft + 120, Y);
            Y += sectionSpacing;

            pdf.text(`Quality Representative's Name: ${selectedQA.SignedByUser}`, marginLeft, Y);
            pdf.text(`Date: ${selectedQA.DateSigned}`, marginLeft + 120, Y);
            Y += sectionSpacing * 2;

            pdf.text('Item Marked Nonconforming: Yes', marginLeft, Y);
            Y += sectionSpacing * 2;

            // Separador
            pdf.setDrawColor(200);
            pdf.line(marginLeft, Y, 198, Y);
            Y += sectionSpacing;

            // Engineering
            pdf.setFontSize(12);
            pdf.text('Engineering:', marginLeft, Y + 5);
            Y += sectionSpacing;

            pdf.setFontSize(10);
            pdf.text(`Review By CF Engineering: ${selectedEng.Review}`, marginLeft, Y + 5);
            pdf.text(`Does Customer require notification of NCR:\n ${selectedEng.NotifyCustomer}`, marginLeft + 120, Y + 5);
            Y += sectionSpacing;

            pdf.text(`The Drawing require update: ${selectedEng.UpdateDrawing}`, marginLeft, Y + 5);
            pdf.text(`Original Rev. Number: ${selectedEng.OriginalRevNumber}`, marginLeft, Y + sectionSpacing + 5);
            pdf.text(`Engineering: ${selectedEng.SignedByUser}`, marginLeft, Y + sectionSpacing * 2 + 5);
            pdf.text(`Date: ${selectedEng.DateSigned}`, marginLeft, Y + 17 + 5);
            pdf.text(`Updated Rev. Number: ${selectedEng.LatestRevNumber}`, marginLeft + 120, Y + sectionSpacing + 5);
            pdf.text(`Revision Date: ${selectedEng.DateSigned}`, marginLeft + 120, Y + sectionSpacing * 2 + 5);
            Y += sectionSpacing * 3;

            
            pdf.line(marginLeft, Y + 15, 198, Y + 15);
            Y += sectionSpacing;

            // Purcharse
            pdf.setFontSize(12);
            pdf.text('Purchasing:', marginLeft, Y + 18);
            Y += sectionSpacing;

            pdf.setFontSize(10);
            pdf.text(`Purchasing's Preliminary Decision: ${selectedPur.Decision}`, marginLeft, Y + 18);
            pdf.text(`Was a CAR raised?: ${selectedPur.CARRaised}`, marginLeft + 120, Y + 18);
            Y += sectionSpacing;

            pdf.text(`Follow-up Required? ${selectedPur.CARRaised}`, marginLeft, Y + 18);
            pdf.text(`Operations Manager: ${selectedPur.SignedByUser}`, marginLeft + 120, Y + 18);
            Y += sectionSpacing;

            pdf.text(`Re-Inspected Acceptable: ${selectedPur.ReInspectAcceptable}`, marginLeft, Y + 18);
            pdf.text(`New NCR Number: ${selectedPur.NewNCRNumber}`, marginLeft + 120, Y + 18);
            Y += sectionSpacing;

            pdf.text(`Inspector's Name: ${selectedPur.SignedByUser}`, marginLeft, Y + 18);
            pdf.text(`NCR Closed: ${modelStatus}`, marginLeft + 120, Y + 18);
            pdf.text(`Quality Department: ${selectedPur.QualityDept}`, marginLeft, Y + sectionSpacing + 18);
            pdf.text(`Date: ${selectedPur.DateSigned}`, marginLeft + 120, Y + sectionSpacing + 18);
            Y += sectionSpacing * 2;

            //Image just dont mess with this is the img on the format that we need 
            const img = 'data:image/png;base64,';
            const imgWidth = 30;
            const imgHeight = 20;
            const x = pdf.internal.pageSize.getWidth() - imgWidth - 10; 
            const y = pdf.internal.pageSize.getHeight() - imgHeight - 10; 
            pdf.addImage(img, 'PNG', x, y, imgWidth, imgHeight);

            // save the  pdf
            pdf.save(`ncr_report_0000${selectedNCR.ID}.pdf`);
        } else {
            console.error('No hay un NCR seleccionado');
        }
}
});
  